import React, { Component, useState, useEffect, Fragment } from 'react';
import { CKEditor } from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
import Button from '@material-ui/core/Button';
import {
    firestore
} from '../firebase/firebase.config'

function TextEditor() {
    const [pupils, setLaw] = useState([]);

    useEffect(() => {
        getLaw();
    }, [])
    const addLaw = async () => {
        const law = document.getElementsByClassName("ck-content").value;
        await firestore.collection("info").add({
          law: law
        }).then(() => {
          document.getElementsByClassName("ck-content").value = "";
          getLaw()
        })
    }

    const getLaw = async () => {
        const data = await firestore.collection("pupils").get();
        setLaw(data.docs.map(doc => ({
          ...doc.data(),
          id: doc.id
        })))
    }

    return (
        <div className="editor">
            <div id="firepad-container"></div>
            <Fragment>
                <CKEditor
                    editor={ ClassicEditor }
                    onReady={ editor => {
                        console.log( 'Editor is ready to use!', editor );
                    } }
                    onChange={ ( event, editor ) => {
                        const data = editor.getData();
                        console.log( { event, editor, data } );
                    } }
                    onBlur={ ( event, editor ) => {
                        console.log( 'Blur.', editor );
                    } }
                    onFocus={ ( event, editor ) => {
                        console.log( 'Focus.', editor );
                    } }
                    id="laws"
                />
                <Button variant="contained" color="primary" component="span" onClick={addLaw()}>ატვირთვა</Button>
            </Fragment>
        </div>
    )
}

export default TextEditor;
