import React, {Fragment, useEffect, useState} from 'react';
import { firebase, auth, firestore } from '../firebase/firebase.config';
import { CKEditor } from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
import TextField from '@material-ui/core/TextField';
import {Editor, EditorState} from 'draft-js';
import Button from '@material-ui/core/Button';
import { BrowserRouter as Router, Switch, Route, Link, useLocation, useParams, Redirect } from "react-router-dom";

function Admin() {
    const [editorState, setEditorState] = React.useState(
        () => EditorState.createEmpty(),
      );
    const [posts, setposts] = useState([]);
    useEffect(() => {
    }, [])
    const addPosts = async () => {
        const title = document.getElementById("title").value;
        const url = document.getElementById("photo").value;
        const content = document.getElementById("content").value;
        // ამატებს ახალ დოკუმენტს ავტომატურად დაგენერირებული ID-ით
        await firestore.collection("posts").add({
            title: title,
            url: url,
            content: content
        }).then(() => {
            document.getElementById("title").value = "";
            document.getElementById("photo").value = "";
            document.getElementById("content").value = "";
        })
    }

    return (
        <div className="admin-news">
            <div className="post-form">
                <form action="" className="add-post">
                    <br />
                    <br />
                    <h2 style={{textAlign: "center"}}>პოსტის დამატება</h2>
                    <br />
                    <br />
                    <TextField id="title" label="სათაური" variant="filled" style={{width: "100%"}}/>
                    <br />
                    <br />
                    <TextField id="photo" label="ფოტოს ლინკი" variant="filled" style={{width: "100%"}}/>
                    <br />
                    <br />
                    <TextField
                        label="პოსტი"
                        multiline
                        rows={5}
                        variant="filled"
                        style={{width: "100%"}}
                        id="content"
                    />
                    <Button variant="contained" color="primary" onClick={() => addPosts()} style={{textAlign: "center"}}>პოსტის დამატება</Button>
                </form>
            </div>
            <br />
            <br />
            <div className="posts">
                <div className="single">
                    <div className="image">
                        <div className="background-post" style={{backgroundImage: "url('https://scontent.ftbs3-1.fna.fbcdn.net/v/t1.6435-9/81999616_2659071587502135_3504164439853105152_n.jpg?_nc_cat=101&ccb=1-3&_nc_sid=730e14&_nc_eui2=AeEeAvz2NI8L1CxILRk8pyW0fE8v6FL0zmB8Ty_oUvTOYGBuB_LQgekqWkboOeyTrmhOi720K6cOek_thqUrZfgK&_nc_ohc=iU10UvhHYlYAX_j8tyd&_nc_ht=scontent.ftbs3-1.fna&oh=5776259aa8a291c1a445f059689078ee&oe=60A5B558')", backgroundSize: "cover", width: "100%"}}></div>
                    </div>
                    <div className="content">
                        <div className="title">
                            <h3>რაღაც</h3>
                        </div>
                        <div className="full">
                            <Link to="/single">იხილე სრულად</Link>
                        </div>
                    </div>
                </div>
            </div>
            <Switch>
                <Route path="/single">
                    <SinglePage />
                </Route>
            </Switch>
        </div>
    )
    function SinglePage() {
        return "კი";
    }
}

export default Admin
